(()=>{"use strict";document.addEventListener("DOMContentLoaded",(()=>{if(window.location.pathname.endsWith("index.html")||"/"===window.location.pathname){const t=document.getElementById("read-more");t&&t.addEventListener("click",(function(){document.querySelectorAll(".toggle-content").forEach((function(e){e.classList.contains("show")?(e.classList.remove("show"),t.textContent="Read more..."):(e.classList.add("show"),t.textContent="Less...")}))}))}}))})();